               document.addEventListener("DOMContentLoaded", function() {
                  let originalTitle = document.title;
                  let inactivityTime = 60000; // 1 minuto
                  let timer;
                  let counter = 1;
                  let isOriginalTitle = true;

                  function resetTimer() {
                      clearTimeout(timer);
                      document.title = originalTitle;
                      counter = 1; // Reinicia el contador
                      isOriginalTitle = true;
                      timer = setTimeout(showNotification, inactivityTime);
                  }

                  function showNotification() {
                      setInterval(() => {
                          document.title = isOriginalTitle ? "New (" + counter + ") " + originalTitle : originalTitle;
                          isOriginalTitle = !isOriginalTitle;
                      }, 1000); // Cambia el título cada 1 segundo para el parpadeo
                      counter++;
                      timer = setTimeout(showNotification, inactivityTime); // Continúa incrementando el contador
                  }

                  // Eventos de interacción del usuario
                  document.addEventListener("mousemove", resetTimer);
                  document.addEventListener("keypress", resetTimer);
                  document.addEventListener("mousedown", resetTimer);  // Detecta clics del mouse
                  document.addEventListener("touchstart", resetTimer); // Detecta toques en pantallas táctiles
                  document.addEventListener("scroll", resetTimer);     // Detecta desplazamientos

                  // Iniciar el temporizador la primera vez
                  timer = setTimeout(showNotification, inactivityTime);
              });

