    // REMOVE ADFLY
        document.addEventListener("DOMContentLoaded", function() {
        const urlToRemove = 'http://adf.ly/5142286/';

        // Función para eliminar la URL dentro de los enlaces (atributos href)
        function removeURLFromHref(node) {
            if (node.nodeType === Node.ELEMENT_NODE && node.tagName === 'A') {
                const href = node.getAttribute('href');
                if (href && href.includes(urlToRemove)) {
                    // Eliminar solo la URL de adf.ly manteniendo el resto
                    const updatedHref = href.replace(urlToRemove, '');
                    node.setAttribute('href', updatedHref);
                }
            }
        }

        // Función para reemplazar la URL tanto en el contenido textual como en los atributos href
        function removeURLFromTextAndAttributes(node) {
            if (node.nodeType === Node.TEXT_NODE) {
                // Remover URL del contenido textual
                node.nodeValue = node.nodeValue.replace(urlToRemove, '');
            } else if (node.nodeType === Node.ELEMENT_NODE) {
                // Revisar el atributo href si es un enlace
                if (node.tagName === 'A') {
                    removeURLFromHref(node);
                }
                // Revisar los nodos hijos
                node.childNodes.forEach(removeURLFromTextAndAttributes);
            }
        }

        // Ejecutar la eliminación inicial en el cuerpo
        document.body.childNodes.forEach(removeURLFromTextAndAttributes);

        // Usar MutationObserver para detectar cambios en el DOM
        const observer = new MutationObserver(function(mutationsList) {
            for (let mutation of mutationsList) {
                if (mutation.type === 'childList') {
                    mutation.addedNodes.forEach(removeURLFromTextAndAttributes);
                } else if (mutation.type === 'attributes' && mutation.attributeName === 'href') {
                    removeURLFromHref(mutation.target);
                }
            }
        });

        // Observar el DOM para cambios
        observer.observe(document.body, {
            childList: true,
            subtree: true,
            attributes: true,
            attributeFilter: ['href'] // Observa específicamente los atributos href
        });
    });


