  var tableElement = document.getElementById("postHere").querySelector("table");

  // Verifica si el elemento de la tabla existe
  if (tableElement) {
    // Crea un nuevo elemento <div>
    var divElement = document.createElement("div");

    // Recorre los elementos de la tabla y reemplaza cada uno por un elemento <div>
    for (var i = 0; i < tableElement.childNodes.length; i++) {
      var childNode = tableElement.childNodes[i];
      if (childNode.nodeName === "TBODY" || childNode.nodeName === "TR" || childNode.nodeName === "TD") {
        var newElement = document.createElement("div");
        newElement.innerHTML = childNode.innerHTML;
        divElement.appendChild(newElement);
      }
    }

    // Reemplaza la tabla por el nuevo elemento <div>
    tableElement.parentNode.replaceChild(divElement, tableElement);
  }
//
